import React, { Component } from 'react';
import $ from "jquery";

class Login extends Component {
  constructor(){
    super();
    this.signinSubmit = this.signinSubmit.bind(this);
    this.handleValueChanged = this.handleValueChanged(this);
    this.state = {
      email : '',
      password : ''
    }
  }

  signinSubmit(e){
    var xhttp = new XMLHttpRequest();
    var url = "http://localhost/my-react-app/src/php/signin_action.php";
    var data = `email=${this.state.email}&password=${this.state.password}`
    var urlData = url+"?"+data;
    xhttp.open("GET", urlData, true);
    xhttp.send();
    xhttp.onreadystatechange = function(){
        if(this.readyState === 4 && this.status === 200){
            var res = JSON.parse(this.responseText);
            if(res["status"] === 200){
                $('#signinForm')[0].reset();
                window.location.replace('/home'); //replaces the current location
                //window.location = '../public/home.php'; //navigates to another location
            }else{
                alert(res["message"]);
            }
        }
    };
    e.preventDefault();
  }
  
  handleValueChanged = tags => (event) =>{
    console.log(tags);
    switch(tags)
    {
        case 'email':
            this.setState({email: event.target.value})
            break;
        case 'password':
            this.setState({password: event.target.value})
            break;
        default:
            break;
    }
  }

  componentDidMount() {
    console.log("login component did mount");
  }

  componentWillUnmount(){
    console.log("login component will unmount");
  }
  
  render() {
    return (
        <div className="signup-form">
            <form id="signinForm" method="get" onSubmit={this.signinSubmit}>
                <h2>Sign In</h2>
                <p>Please fill in this form to sign in to your account!</p>
                <hr/>
                <div className="form-group">
                    <input type="email" className="form-control" name="email" id="email" placeholder="Email" required="required" onChange={this.handleValueChanged("email")} value={this.state.email}/>
                </div>
                <div className="form-group">
                    <input type="password" className="form-control" name="password" id="password" placeholder="Password" required="required" onChange={this.handleValueChanged("password")} value={this.state.password}/>
                </div>
                <div className="form-group">
                    <input type="submit" name="btnsignin" className="btn btn-primary btn-lg" value="Sign In"/>
                </div>
            </form>
            <div className="hint-text">Don't have an account? <a href="/signup">Sign up here</a></div>
        </div>
    );
  }
}
  
export default Login;
